INSERT INTO set_of_notice_templates VALUES 
  (1, 'Общая оболочка письма', 'Новости на проекте СП 2.0', '<!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01//EN" "http://www.w3.org/TR/html4/strict.dtd">\r\n<html>\r\n<head>\r\n<meta http-equiv="Content-Type" content="text/html; charset=UTF-8">\r\n</head>\r\n<body style="font: 14px Arial;color:#666;">\r\n<table cellpadding=0 cellspacing=0>\r\n<tr>\r\n<td style="width:60px;height:50px;margin:10px;padding:17px 9px 13px 11px;background:#c00;border-bottom: solid 2px #900;border-right: solid 2px #900;font:bold 40px Arial;color:#fff;text-shadow: #900 2px 3px 1px;">СП</td>\r\n<td style="padding-left:10px;"><a href="{$domain}" style="font:bold 20px Arial;color:#666;">Совместные покупки 2.0</a></td> \r\n</tr>\r\n</table>\r\n<h1 style="text-align:center;">Здравствуйте{if isset($user_name_or_login)}, {$user_name_or_login}{/if}!</h1>\r\n{$body}\r\n<p>C уважением, Администрация проекта СП 2.0<br />\r\n----<br />\r\nsupport@sp2all.ru</p>\r\n<hr style="height:1px;background-color:#b6b6b6;border:none;" />\r\n<p style="font-size:80%">\r\nПодпишитесь <b>ВКонтакте</b> <a href="http://vkontakte.ru/sp2all">http://vkontakte.ru/sp2all</a><br />\r\nПрисоединитесь к группе на <b>Одноклассниках</b> <a href="http://odnoklassniki.ru/group/51597825212658">http://odnoklassniki.ru/group/51597825212658</a><br />\r\nЧитайте наш <b>Livejournal</b> <a href="http://sp2all.livejournal.com/">http://sp2all.livejournal.com/</a><br />\r\nСледуйте за нами на <b>Twitter</b> <a href="http://twitter.com/sp2all">http://twitter.com/sp2all</a><br />\r\nСтаньте фанатом на <b>Facebook</b> <a href="http://www.facebook.com/groups/sp2all.ru">http://www.facebook.com/groups/sp2all.ru</a><br />\r\nВступите в сообщество <b>Мой Мир</b> <a href="http://my.mail.ru/community/sp2all.ru/">http://my.mail.ru/community/sp2all.ru/</a></p>\r\n<p style="text-align:center; margin-top:3px;">\r\n<a href="http://{$domain}/profile/subscribe/">Отписаться от рассылки</a></p>\r\n</body>\r\n</html>', '2012-06-19 15:25:21', 0, NULL),
  (2, 'СП в след. статус', 'СП№ {$sp_id} переведена в следующий статус', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a> перевел <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> в статус "{$sp_status}"</td>\r\n</tr>\r\n</table> \r\n', '2012-02-28 08:34:18', 2, 'notice_sp_state'),
  (5, 'СП в сбор предоплаты СМС', 'Сбор предоплаты', 'СП{$sp_id}: Сбор предоплаты', '2012-02-28 08:34:36', 1, 'notice_sp_state_sms'),
  (6, 'СП завершено. Карма. Участникам.', 'СП завершена. Оцените участников', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\n<a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> завершено. <br>\r\n<a href="http://{$domain}/org/sp/{$sp_id}/customers/">Оцените участников</a>\r\n</td>\r\n</tr>\r\n</table> \r\n', '2012-03-06 11:26:16', 2, 'notice_karma_state'),
  (7, 'СП вернули в прием заказов', 'СП№{$sp_id} возвращена в Прием заказов', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a> вернул <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> в статус "Прием заказов".<br>\r\nВы можете дозаказать товар из этой закупки.</td>\r\n</tr>\r\n</table> ', '2012-03-12 08:14:55', 1, 'notice_sp_state'),
  (8, 'Участник внес предоплату', 'Участник {$user_login} внес предоплату', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\nУчастник <a href="http://{$domain}/users/{$user_login}/">{$user_login}</a>  <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> внес предоплату.</td>\r\n</tr>\r\n</table> \r\n', '2012-03-15 14:48:19', 1, 'notice_sp_user_prepayment'),
  (9, 'Участник внес предоплату СМС', 'Участник внес предоплату СМС', 'Участник СП{$sp_id} {$user_login} внес предоплату', '2012-02-28 08:38:04', 1, 'notice_sp_org_sms'),
  (10, 'СП завершена. Карма. Оргу.', 'СП №{$sp_id} завершена. Оцените работу организатора', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\n<a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> завершена, теперь Вы можете выставить оценку работы организатора <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a>.</td>\r\n</tr>\r\n</table> ', '2012-03-06 11:26:24', 2, 'notice_karma_state'),
  (11, 'В СП добавлен заказ', 'Участник СП №{$sp_id} сделал заказ', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\nУчастник <a href="http://{$domain}/users/{$user_login}/">{$user_login}</a>  <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> сделал  заказ.</td>\r\n</tr>\r\n</table> \r\n', '2012-02-28 08:39:43', 1, 'notice_sp_user_in'),
  (12, 'В СП добавлен заказ СМС', 'В СП добавлен заказ', 'В СП №{$sp_id} добавлен заказ', '2012-02-01 08:00:13', 1, NULL),
  (13, 'Заказ отредактирован Оргом', 'Организатор отредактировал Ваш заказ', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a>  <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> отредактировал Ваш заказ.</td>\r\n</tr>\r\n</table> \r\n', '2012-03-06 11:21:55', 1, 'notice_order_state'),
  (14, 'Заказ отредактирован Участником', 'Участник отредактировал свой заказ', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\nУчастник <a href="http://{$domain}/users/{$user_login}/">{$user_login}</a>  <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> отредактировал свой заказ.</td>\r\n</tr>\r\n</table> \r\n', '2012-02-28 08:45:19', 1, 'notice_sp_user_in'),
  (15, 'Заказ удален Оргом', 'Организатор удалил Ваш заказ', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a>  <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> удалил Ваш заказ.<br>\r\nПопробуйте <a href="http://{$domain}/sp/">подобрать другой товар</a>.</td>\r\n</tr>\r\n</table> \r\n', '2012-03-06 11:22:06', 1, 'notice_order_state'),
  (16, 'Заказ удален Участником', 'Участник отказался от заказа', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\nУчастник <a href="http://{$domain}/users/{$user_login}/">{$user_login}</a>  в <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> отказался от заказа.<br>\r\nЕсли в Вашей закупке не хватает участников, Вы можете <a href="http://{$domain}/org/create/advert/"> прорекламировать её</a>.</td>\r\n</tr>\r\n</table> ', '2012-02-28 10:26:42', 1, 'notice_sp_user_out'),
  (17, 'В СП добавлен новый товар Участником', 'Участник добавил в СП №{$sp_id} новый товар', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\nУчастник <a href="http://{$domain}/users/{$user_login}/">{$user_login}</a> добавил новый товар в <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a>./td>\r\n</tr>\r\n</table> ', '2012-02-28 10:25:50', 1, 'notice_sp_user_in'),
  (18, 'Добавлена встреча', 'Назначена встреча  по СП №{$sp_id}', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a> <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> назначил встречу {$address} {$datetime|date_format:"%H:%M %d.%m.%y"}.</td>\r\n</tr>\r\n</table> ', '2012-03-06 11:41:24', 1, 'notice_sp_state'),
  (19, 'Время перевода СП в следующий статус истекает', 'Истекает время до перевода СП №{$sp_id} в статус {$sp_status}', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nНе позднее, чем через {$time} дня, <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> должна быть переведена в статус {$sp_status}.</td>\r\n</tr>\r\n</table> ', '2012-03-06 11:45:11', 2, 'notice_sp_force_state'),
  (20, 'СП принудительно переведена в статус', 'СП №{$sp_id} принудительно переведена в статус {$sp_status} ', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\n<a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> \r\nпринудительно переведена в статус {$sp_status}.</td>\r\n</tr>\r\n</table> ', '2012-03-24 11:35:16', 1, 'notice_sp_force_state'),
  (21, 'СП отменена Администрацией', 'СП №{$sp_id} отменена Администрацией', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\n<a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> отменена Администрацией за нарушения правил.</td>\r\n</tr>\r\n</table> ', '2012-03-06 11:45:29', 1, 'notice_sp_force_state'),
  (22, 'Истекает срок действия подарочного купона', 'Истекает срок действия подарочного купона', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nВнимание!<br>\r\nЧерез {$time} дня истекает срок действия подарочного купона.<br>\r\nВы можете использовать его, вступив в одну из <a href="http://{$domain}/sp/">множества закупок</a>.</td>\r\n</tr>\r\n</table> \r\n\r\n', '2012-03-06 12:36:52', 2, 'notice_coupon_state'),
  (24, 'Напоминание о встрече оргу', 'Встреча  по СП', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\nУ Вас встреча по:<br>\r\n{section name=sp loop=$sp}\r\n<a href="http://{$domain}/sp/{$sp.id}/">СП №{$sp.id}</a><br>\r\n{/section}\r\n {$text_20} через {$minutes} минут.</td>\r\n</tr>\r\n</table> ', '2012-05-10 11:43:51', 1, 'notice_meeting_reminder'),
  (25, 'Недостаточно купонов. Рассылка СМС прекращена', 'Рассылка СМС приостановлена', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nРассылка СМС приостановлена, т.к. у Вас на счете закончились купоны.<br>\r\nДля возобновления рассылки необходимо срочно <a href="http://{$domain}/coupons/buy/">пополнить счет</a>.<br>\r\nВсе пропущенные сообщения отправлены не будут!</td>\r\n</tr>\r\n</table> ', '2012-03-06 12:37:13', 1, 'notice_coupon_state'),
  (26, 'СП в след. статус (Подписка)', 'СП № {$sp_id} на модерации', '{$sp_comm}\r\n', '2012-09-05 14:35:57', 0, 'notice_sp_podpiska'),
  (27, 'СП вернули в прием заказов (Подписка)', 'СП № {$sp_id} возвращена в Прием заказов', '<p>Информация по подписке:</p>\r\n<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a> вернул <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> в статус "Прием заказов".<br>\r\nВы можете дозаказать товар из этой закупки.</td>\r\n</tr>\r\n</table>', '2012-03-06 12:57:52', 2, 'notice_sp_podpiska'),
  (28, 'Вам пришло личное сообщение', 'Вам пришло личное сообщение', '<p>Вам пришло личное сообщение от <a href="http://{$domain}/users/{$user_login}/">{$user_login}</a>:</p>\r\n\r\n<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">{$message_text}</td>\r\n</tr>\r\n</table> \r\n\r\n<p>Вы можете <a href="http://{$domain}/messages/#{$user_login}">ответить на него на сайте</a>.</p>', '2012-03-06 13:25:05', 1, 'notice_ls_notice'),
  (29, 'Вам пришло личное сообщение (СМС)', 'Вам пришло личное сообщение', 'ЛС от {$user_login}:{$message_text_20}', '2012-03-06 13:24:54', 1, 'notice_ls_sms'),
  (30, 'Открыта повторная закупка с этого сайта', 'Открыта повторная закупка с этого сайта', '<p style="width:700px;background-color:#eeeeee;border:solid 2px #316ac5;\r\npadding:10px;font-size:140%;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a> открыл <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id}</a> с того же сайта <a href="{$site}">{$site}</a>, что и Ваша закупка <a href="http://{$domain}/sp/{$sp_id2}/">СП №{$sp_id2}</a>.<br>\r\nЕсли в Вашей закупке не хватает участников, Вы можете <a href="http://{$domain}/org/create/advert/"> прорекламировать её</a>.</p>', '2012-03-06 12:45:46', 1, 'notice_site_reminder'),
  (31, 'Ваша предоплата отмечена полученной', 'Ваша предоплата отмечена полученной', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a> <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> подтвердил получение от Вас предоплаты в сумме <b>{$prepayment_ammount}</b>.</td>\r\n</tr>\r\n</table> \r\n', '2012-03-06 12:38:11', 1, 'notice_sp_state'),
  (32, 'Рассылка модераторам по стоп-словам', 'Обратите внимение', '<p>Обратите внимание:</p>\r\n<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">{$object_id} - Номер объекта<br>\r\n{$object_type} - Тип объекта<br>\r\n{$user_login} - Логин пользователя<br>\r\n{$text} - Текст со стоп-словом<br>\r\n</td>\r\n</tr>\r\n</table> ', '2012-02-02 05:31:52', 1, NULL),
  (33, 'Ваш реферал совершил успешную закупку', 'Привлеченный Вами участник совершил успешную покупку', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nПривлеченный Вами участник \r\n<a href="http://{$domain}/users/{$user_login}/">{$user_login}</a> совершил успешную покупку.<br>\r\nМы благодарим Вас и дарим Вам купон.<br>\r\nУдачного Вам дня и хорошего настроения!</td>\r\n</tr>\r\n</table>', '2012-03-06 12:47:20', 3, 'notice_referal'),
  (34, 'Вас добавили в друзья', 'Вас добавили в друзья', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nУчастник <a href="http://{$domain}/users/{$user_login}/">{$user_login}</a> добавил Вас в друзья.<br>\r\nВы сможете участвовать в любых его закупках вне зависимости от Вашей кармы.<br>\r\nВы можете <a href="http://{$domain}/users/{$user_login}/#add_frend">добавить {$user_login} в свои друзья</a> или <a href="http://sp4all.ru:8080/messages/#{$user_login}">написать ему сообщение</a>.</td>\r\n</tr>\r\n</table> ', '2012-03-06 12:52:36', 1, 'notice_friend_state'),
  (35, 'Товар предложенный в СП отклонен организатором', 'Организатор отклонил добавление в закупку вашего товара', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a>  <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> ОТКЛОНИЛ добавление {$sp_product_title} в закупку.<br>\r\nПопробуйте <a href="http://{$domain}/sp/">подобрать другой товар</a>.</td>\r\n</tr>\r\n</table> ', '2012-03-06 12:38:28', 1, 'notice_sp_state'),
  (36, 'Товар одобрен организатором', 'Организатор одобрил добавление товара', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a>  <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> одобрил добавление <a href="http://{$domain}/sp/{$sp_id}/item/{$sp_product_id}/">{$sp_product_title}</a> в закупку.</td>\r\n</tr>\r\n</table> ', '2012-03-06 12:38:38', 1, 'notice_sp_state'),
  (37, 'Время показа вашего рекламного объявления истекло', 'Время показа вашего рекламного объявления истекло', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nВремя показа вашего рекламного объявления истекло.<br>\r\nОно было показано {$Count1} раз, по нему было {$Count2} переходов.<br>\r\nВы можете <a href="http://{$domain}{$advert}">продлить рекламу</a>.</td>\r\n</tr>\r\n</table> ', '2012-03-06 12:50:44', 1, 'notice_ad_state'),
  (38, 'Добавлена встреча СМС', 'Добавлена встреча', 'Организатор СП №{$sp_id} назначил встречу {$datetime} по адресу: {$address_8}.', '2012-02-28 10:28:59', 1, 'notice_sp_state_sms'),
  (39, 'Напоминание о встрече оргу СМС', 'Встреча  по СП', 'У Вас встреча по СП №: {section name=sp loop=$sp}{$sp.id},{/section} {$text_20} через {$minutes} минут.', '2012-05-10 11:42:52', 1, NULL),
  (40, 'Новый коментарий в обсуждении сп', 'Новый комментарий в обсуждении СП №{$sp_id}', '<p>Новый комментарий в обсуждении <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a>:</p>\r\n<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">{$user_login}: {$comment}</td>\r\n</tr>\r\n</table> ', '2012-03-06 13:17:59', 1, 'notice_new_coment'),
  (41, 'Новый коментарий в обсуждении товара', 'Новый комментарий в обсуждении товара', '<p>Новый комментарий в обсуждении товара  <a href="http://{$domain}/sp/{$sp_id}{$product_path}/">{$product_name}</a> <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a>:</p>\r\n<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">{$user_login}: {$comment}</td>\r\n</tr>\r\n</table> ', '2012-03-06 13:18:18', 1, 'notice_new_coment'),
  (42, 'Показ рекламы остановлен администрацией', 'Показ объявления приостановлен Администрацией', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nПоказ объявления №{$ad_id} к <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> приостановлен Администрацией. <br>\r\nКомментарий: {$comment}</td>\r\n</tr>\r\n</table> ', '2012-03-06 12:50:57', 1, 'notice_ad_state'),
  (43, 'Объявление успешно прошло модерацию', 'Объявление успешно прошло модерацию', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nВаше объявление №{$ad_id} к <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> успешно прошло модерацию и теперь показывается на главной странице.</td>\r\n</tr>\r\n</table> ', '2012-03-06 12:51:07', 1, 'notice_ad_state'),
  (44, 'Объявление не прошло модерацию', 'Объявление не прошло модерацию', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nВаше объявление №{$ad_id} к <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> не прошло модерацию. <br>\r\nКомментарий: {$comment}</td>\r\n</tr>\r\n</table> ', '2012-03-06 12:51:23', 1, 'notice_ad_state'),
  (45, 'Новостная рассылка', 'Новости СП', '<!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01//EN" "http://www.w3.org/TR/html4/strict.dtd">\r\n<html>\r\n<head>\r\n<meta http-equiv="Content-Type" content="text/html; charset=UTF-8">\r\n</head>\r\n<body style="font: 14px Arial;color:#666;">\r\n<table cellpadding=0 cellspacing=0>\r\n<tr>\r\n<td style="width:60px;height:50px;margin:10px;padding:17px 9px 13px 11px;background:#c00;border-bottom: solid 2px #900;border-right: solid 2px #900;font:bold 40px Arial;color:#fff;text-shadow: #900 2px 3px 1px;">СП</td>\r\n<td style="padding-left:10px;"><a href="{$domain}" style="font:bold 20px Arial;color:#666;">Совместные покупки 2.0</a></td> \r\n</tr>\r\n</table>\r\n<h1 style="text-decoration:underline;">Новая реферальная программа СП "Приведи друга!"</h1>\r\n<p>Уважаемые Участники и Организаторы!</p>\r\n<p>Поздравляем Вас с праздником!</p>\r\n<p>С радостью сообщаем Вам, что сегодня, в День святого Валентина и в преддверии наступающих праздников (23 февраля и 8 марта), \r\nмы объявляем о запуске новой реферальной программы для всех наших пользователей!</p>\r\n<p>Теперь Вы можете еще более просто и удобно приглашать своих друзей в СП и внимание!.. получать пожизненный доход!</p>\r\n<p>Кроме того, приглашенный Вами пользователь также получает бонус!</p>\r\n<p>Таким образом, с новой реферальной программой выигрывают все!</p>\r\n<p><b>Как это работает?</b></p>\r\n<ul>\r\n    <li>Вы приглашаете своих друзей и знакомых устно, по почте, icq, skype, в соц. сетях.</li>\r\n    <li>При этом Вы просто сообщаете свой ник <b>{$user_login}</b> в системе.</li>\r\n    <li>При регистрации Ваш друг указывается Ваш ник в качестве бонусного слова и таким образом закрепляется за Вами и получает бонусный купон.</li>\r\n    <li>Старые реферальные ссылки работают по новой системе. При переходе по старой реф.ссылке новый участник получит бонусный купон и будет приносить Вам доход.</li>\r\n</ul>\r\n<p><b>Что получаете Вы?</b></p>\r\n<p><b>с 14.02.2012 по 01.05.2012</b></p>\r\n<ul>\r\n    <li>1 купон за каждую успешно завершенную закупку Вашего друга в качестве участника СП;</li>\r\n    <li>вознаграждение начисляется в виде подарочных купонов в конце каждого месяца.</li>\r\n</ul>\r\n<p><b>После 01.05.2012</b></p>\r\n<ul>\r\n    <li>10% от всех потраченных не подарочных купонов в текущем месяце приглашенного Вами пользователя!</li>\r\n    <li>вознаграждение начисляется в виде целых подарочных купонов в конце каждого месяца.</li>\r\n</ul>\r\n<p><b>Что получает приглашенный?</b></p>\r\n<ul>\r\n    <li>1 купон в подарок за регистрацию через Вас.</li>\r\n</ul>\r\n<p><u>Срок действия всех подарочных купонов - 1 месяц!</u></p>\r\n<p>Мы надеемся, что это программа поможет создать еще более мощное и активное сообщество любителей СП!</p>\r\n<p>Мы также активно продолжаем работать над развитием и продвижением проекта.</p>\r\n<p><b>Последние изменения на сайте:</b></p>\r\n<ul>\r\n    <li>возврат купонов сразу: при выходе и при переходе СП в Стоп;</li>\r\n    <li>поддержка BB-кодов в обсуждениях СП и товаров, загрузка картинок, смайлики;</li>\r\n    <li>значительно увеличена скорость работы системы;</li>\r\n    <li>всем включены рассылки новых открытых СП. Настроить рассылку можно <a href="http://{$domain}/profile/subscribe/">в личном кабинете</a> в правом столбце.</li>\r\n</ul>\r\n<p>Следите за анонсами на сайте <a href="http://{$domain}">http://{$domain}</a>!</p>\r\n<p>Если у Вас есть какие-то идеи, замечания и предложения, пожалуйста, оставляйте их через нашу форму обратной связи <a href="http://{$domain}/fb/">http://{$domain}/fb/</a></p>\r\n<p>Спасибо, что Вы с нами!</p>\r\n<p>C уважением, Администрация проекта СП 2.0<br />\r\n----<br />\r\nsupport@sp2all.ru</p>\r\n<hr style="height:1px;background-color:#b6b6b6;border:none;" />\r\n<p style="font-size:80%">\r\nПодпишитесь <b>ВКонтакте</b> <a href="http://vkontakte.ru/sp2all">http://vkontakte.ru/sp2all</a><br />\r\nПрисоединитесь к группе на <b>Одноклассниках</b> <a href="http://odnoklassniki.ru/group/51597825212658">http://odnoklassniki.ru/group/51597825212658</a><br />\r\nЧитайте наш <b>Livejournal</b> <a href="http://sp2all.livejournal.com/">http://sp2all.livejournal.com/</a><br />\r\nСледуйте за нами на <b>Twitter</b> <a href="http://twitter.com/sp2all">http://twitter.com/sp2all</a><br />\r\nСтаньте фанатом на <b>Facebook</b> <a href="http://www.facebook.com/groups/sp2all.ru">http://www.facebook.com/groups/sp2all.ru</a><br />\r\nВступите в сообщество <b>Мой Мир</b> <a href="http://my.mail.ru/community/sp2all.ru/">http://my.mail.ru/community/sp2all.ru/</a></p>\r\n<p style="text-align:center; margin-top:3px;">\r\n<a href="http://{$domain}/profile/subscribe/">Отписаться от рассылки</a></p>\r\n</p>\r\n</body>\r\n</html>', '2012-02-28 09:22:16', 0, 'notice_sp_news'),
  (46, 'Обертка регистрационных сообщений', '{$act} на проекте СП 2.0', '<!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01//EN" "http://www.w3.org/TR/html4/strict.dtd">\r\n<html>\r\n<head>\r\n<meta http-equiv="Content-Type" content="text/html; charset=UTF-8">\r\n</head>\r\n<body style="font: 14px Arial;color:#666;">\r\n<table cellpadding=0 cellspacing=0>\r\n<tr>\r\n<td style="width:60px;height:50px;margin:10px;padding:17px 9px 13px 11px;background:#c00;border-bottom: solid 2px #900;border-right: solid 2px #900;font:bold 40px Arial;color:#fff;text-shadow: #900 2px 3px 1px;">СП</td>\r\n<td style="padding-left:10px;"><a href="{$domain}" style="font:bold 20px Arial;color:#666;">Совместные покупки 2.0</a></td> \r\n</tr>\r\n</table>\r\n<h1 style="text-align:center;">Здравствуйте, {$user_name_or_login}!</h1>\r\n{$body}\r\n<p>C уважением, Администрация проекта СП 2.0<br />\r\n----<br />\r\nsupport@sp2all.ru</p>\r\n<hr style="height:1px;background-color:#b6b6b6;border:none;" />\r\n<p style="font-size:80%">\r\nПодпишитесь <b>ВКонтакте</b> <a href="http://vkontakte.ru/sp2all">http://vkontakte.ru/sp2all</a><br />\r\nПрисоединитесь к группе на <b>Одноклассниках</b> <a href="http://odnoklassniki.ru/group/51597825212658">http://odnoklassniki.ru/group/51597825212658</a><br />\r\nЧитайте наш <b>Livejournal</b> <a href="http://sp2all.livejournal.com/">http://sp2all.livejournal.com/</a><br />\r\nСледуйте за нами на <b>Twitter</b> <a href="http://twitter.com/sp2all">http://twitter.com/sp2all</a><br />\r\nСтаньте фанатом на <b>Facebook</b> <a href="http://www.facebook.com/groups/sp2all.ru">http://www.facebook.com/groups/sp2all.ru</a><br />\r\nВступите в сообщество <b>Мой Мир</b> <a href="http://my.mail.ru/community/sp2all.ru/">http://my.mail.ru/community/sp2all.ru/</a></p>\r\n<p style="text-align:center; margin-top:3px;">\r\n<a href="http://{$domain}/profile/subscribe/">Отписаться от рассылки</a></p>\r\n</body>\r\n</html>\r\n\r\n', '2012-02-02 08:22:05', 0, NULL),
  (47, 'Восстановление пароля', 'Восстановление пароля на проекте СП 2.0', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nВы получили это сообщение, потому что Вы или кто-то ошибочно от Вашего имени запросил смену пароля на Вашем аккаунте. <br>\r\nСсылка на страницу изменения пароля: <br><br>\r\n<a href="http://{$domain}/password/change/{$code}/">http://{$domain}/password/change/{$code}/</a><br><br>\r\nВаш логин: {$login}<br>\r\nЭта ссылка будет действительна в течение суток.<br>\r\nЕсли Вы не запрашивали смену пароля - просто проигнорируйте данное письмо.</td>\r\n</tr>\r\n</table> \r\n\r\n', '2012-02-02 05:44:31', 1, NULL),
  (48, 'Регистрация', 'Регистрация на проекте СП 2.0', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nВы зарегистировались на проекте "Совместные покупки 2.0".<br>\r\nДля активации Вашего аккаунта на сайте перейдите по ссылке:<br>\r\n<a href="http://{$domain}/registration/code/{$code}">http://{$domain}/registration/code/{$code}</a><br>\r\nВаш логин: {$login}<br>\r\nВаш пароль: {$password}<br>\r\nОбращаем Ваше внимание, что ссылка будет активна в течение 2 суток.<br>\r\nЕсли Вы не регистрировались на сайте {$domain}, просто проигнорируйте это письмо.</td>\r\n</tr>\r\n</table> \r\n\r\n\r\n', '2012-02-02 05:48:03', 1, NULL),
  (49, 'Уведомление об изменении товара', 'Товар, заказанный Вами в СП №{$sp_id}, изменен организатором', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nТовар <a href="{$path}">"{$product_name}"</a>, заказанный Вами в <a href="/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a>, изменен организатором <a href="/users/{$org_login}/">{$org_login}</a>.</td>\r\n</tr>\r\n</table> ', '2012-03-06 12:40:51', 1, 'notice_sp_state'),
  (50, 'Новый Реферальный пользователь', 'Вы привлекли нового участника', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nПо Вашей рекомендации зарегистрировался новый участник\r\n<a href="http://{$domain}/users/{$user_login}/">{$user_login}</a>.<br>\r\nПомогите ему освоится и совершить свои первые покупки\r\n<br>\r\n<br>\r\nПодробнее о реферальной системе Вы можете прочитать <a href="http://{$domain}/help/chto_takoe_referalnaya_sistema.php" title="Правила Реферальной системы">здесь</a><br>\r\nУдачного Вам дня и хорошего настроения!</td>\r\n</tr>\r\n</table>', '2012-03-06 12:47:36', 3, 'notice_referal'),
  (51, 'Сайт на домене. Запрос Модерации', 'Администратор домена {$to_domain} запросил модерацию {$what}.', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nАдминистратор домена <a href="http://{$to_domain}/">{$to_domain}</a> <a href="http://{$domain}/users/{$user_login}/">{$user_login}</a> запросил модерацию {$what}.\r\n<br><br>\r\n<a href="http://{$domain}/admin/admindomain/?domain={$to_domain}" title="Модерация {$what} на {$to_domain}">Модерация {$what}</a>\r\n</td>\r\n</tr>\r\n</table>', '2012-03-06 13:00:25', 1, 'notice_site_on_domain'),
  (52, 'Сайт на домене. Отказ в размещении', 'Отказ от публикации {$what}', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nУважаемый администратор домена <a href="http://{$to_domain}/">{$to_domain}</a>,<br>Ваш запрос на размещение {$what} был отклонен.<br>Причина:{$comment}.\r\n</td>\r\n</tr>\r\n</table>', '2012-03-06 13:00:35', 1, 'notice_site_on_domain'),
  (53, 'Сайт на домене. Размещение обобрено', 'Запрос на размещение {$what} одобрен.', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nУважаемый администратор домена <a href="http://{$to_domain}/">{$to_domain}</a>,<br>Ваш запрос на размещение {$what} был одобрен.\r\n</td>\r\n</tr>\r\n</table>', '2012-03-06 13:00:43', 1, 'notice_site_on_domain'),
  (54, 'Сайт на домене. Остановка показа', 'Остановка показа  {$what}.', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nУважаемый администратор домена <a href="http://{$to_domain}/">{$to_domain}</a>,<br>Показ {$what} был остановлен.<br>Причина:{$comment}.\r\n</td>\r\n</tr>\r\n</table>', '2012-03-06 13:00:56', 1, 'notice_site_on_domain'),
  (55, 'Смена e-mail', 'Смена почтового ящика на проекте СП 2.0', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee; border:solid 2px #316ac5;font:140% Arial;">\r\nВы запросили смену адреса электронной почты на проекте "Совместные покупки 2.0".<br>\r\nДля активации нового почтового адреса перейдите по ссылке:<br>\r\n<a href="http://{$domain}/email/{$email}/code/{$code}">http://{$domain}/email/{$email}/code/{$code}</a><br>\r\n\r\nОбращаем Ваше внимание, что ссылка будет активна в течение 2 суток.<br>\r\nЕсли Вы не регистрировались на сайте {$domain}, просто проигнорируйте это письмо.</td>\r\n</tr>\r\n</table> \r\n\r\n\r\n', '2012-03-20 14:06:47', 1, NULL),
  (56, 'Обслуживание сайта прекращено', 'Обслуживание сайта {$old_domain} прекращено', 'Обслуживание сайта <a href="http://{$old_domain}">{$old_domain}</a> прекращено.<br>\r\nВаш аккаунт вместе со всей вашей историей, закупками и кармой перенесен на сайт <a href="http://{$new_domain}">{$new_domain}</a><br>\r\nПриятных вам покупок!', '2012-03-30 15:49:51', 1, NULL),
