  (58, 'Изменена встреча СМС', 'Изменена встреча', 'Организатор СП №{$sp_id} извенил встречу {$datetime} по адресу: {$address_8}.', '2012-02-28 10:28:59', 1, 'notice_sp_state_sms'),
  (59, 'Отменена встреча', 'Отменена встреча  по СП №{$sp_id}', '<table cellpadding=0 cellspacing=0 style="margin:20px 0px;">\r\n<tr>\r\n<td style="width:680px;padding:10px;background:#eeeeee;border:solid 2px #316ac5;font:140% Arial;">\r\nОрганизатор <a href="http://{$domain}/users/{$org_login}/">{$org_login}</a> <a href="http://{$domain}/sp/{$sp_id}/">СП №{$sp_id} "{$sp_title}"</a> отменил встречу {$address} {$datetime|date_format:"%H:%M %d.%m.%y"}.</td>\r\n</tr>\r\n</table> ', '2012-03-06 11:41:24', 1, 'notice_sp_state'),
